import { createRouter, createWebHistory } from "vue-router";
import guardRouter from "./guard-router.js";

// 1. Define route components.
import Home from "../views/Home.vue";
import Freelancer from "../views/Freelancer";
import Teacher from "../views/Teacher";
import Ministry from "../views/Ministry";
import Company from "../views/Company";
import Login from "../views/Login.vue";
import Error from "../views/Error.vue";
import HomePage from "../components/GlobalComponent/HomePage.vue";

// Company router
import FreelancersRegister from "../views/company/FreelancersRegister.vue";
import FreelancersTraining from "../views/company/FreelancersTraining.vue";
import TaskDetail from "../views/company/TaskDetail.vue";
import UpdateInforCompany from "../components/Company/UpdateInfor.vue";

//Teacher router
import TeacherManage from "../views/teacher/TeacherManage.vue";
import TeacherView from "../views/teacher/TeacherView.vue";
import TeacherAssess from "../components/Teacher/ProgressAssess/TeacherAssess.vue";
import ViewProgress from "../components/GlobalComponent/ViewProgress.vue";

// Ministry router
import TeacherManagement from "../views/ministry/TeacherManagement.vue";
import CompanyManagement from "../views/ministry/CompanyManagement.vue";
import FreelancersManagement from "../views/ministry/FreelancersManagement.vue";

//Freelancer router
import FreelancerRegister from "../views/freelancer/FreelancerRegister.vue";
import FreelancerManage from "../views/freelancer/FreelancerManage.vue";
import UpdateInfor from "../views/UpdateInfor.vue";
import ViewAssessCompany from "../components/GlobalComponent/ViewAssessCompany.vue";
import ViewAssessTeacher from "../components/Freelancer/ManageInternship/ViewAssessTeacher.vue";

// Admin router
import Admin from "../views/Admin";
import AdminHome from "../views/admin/AdminHome.vue";
import AdminManage from "../views/admin/AdminManage.vue";
// 2. Define some routes

const routes = [
    { path: "/", component: Home },
    { path: "/login", component: Login },
    { path: "/freelancer", component: Freelancer },
    { path: "/error", component: Error },

    {
        path: "/admin",
        component: Admin,
        children: [
            { path: "/admin/home", component: AdminHome },
            { path: "/admin/manage", component: AdminManage },
        ],
    },
    {
        path: "/teacher",
        component: Teacher,
        children: [
            { path: "/teacher/home", component: HomePage },
            { path: "/teacher/manage", component: TeacherManage },
            {
                path: "/teacher/view",
                component: TeacherView,
                children: [
                    {
                        path: "/teacher/view/progress/:id",
                        component: ViewProgress,
                    },
                    {
                        path: "/teacher/view/company-assess/:id",
                        component: ViewAssessCompany,
                    },
                    {
                        path: "/teacher/view/assess-freelancer/:id",
                        component: TeacherAssess,
                    },
                ],
            },
            {
                path: "/teacher/update-infor/:status",
                component: UpdateInfor,
            },
        ],
    },
    {
        path: "/company",
        component: Company,
        children: [
            { path: "/company/home", component: HomePage },
            { path: "/company/freelancers-register", component: FreelancersRegister },
            { path: "/company/detail-task/:freelancerId", component: TaskDetail },
            { path: "/company/freelancers-training", component: FreelancersTraining },
            {
                path: "/company/update-infor/:status",
                component: UpdateInforCompany,
            },
        ],
    },
    {
        path: "/ministry",
        component: Ministry,
        children: [
            { path: "/ministry/home", component: HomePage },
            {
                path: "/ministry/teacher-management",
                component: TeacherManagement,
            },
            {
                path: "/ministry/teacher-management/:teacherId",
                component: FreelancersManagement,
            },
            {
                path: "/ministry/company-management",
                component: CompanyManagement,
            },
        ],
    },

    {
        path: "/freelancer",
        component: Freelancer,
        children: [
            { path: "/freelancer/home", component: HomePage },
            { path: "/freelancer/register", component: FreelancerRegister },
            {
                path: "/freelancer/manage",
                component: FreelancerManage,
                children: [
                    {
                        path: "/freelancer/manage/view-progress",
                        component: ViewProgress,
                    },
                    {
                        path: "/freelancer/manage/view-assess-company",
                        component: ViewAssessCompany,
                    },
                    {
                        path: "/freelancer/manage/view-assess-teacher",
                        component: ViewAssessTeacher,
                    },
                ],
            },
            {
                path: "/freelancer/update-infor/:status",
                component: UpdateInfor,
            },
        ],
    },
];

// 3. Create the router instance and pass the `routes` option

const router = createRouter({
    history: createWebHistory(),
    routes,
    linkActiveClass: "active-link",
});

guardRouter(router);

export default router;
